import React, { useState } from 'react';
import './App.css';

function MyButton() {

  const [count, setCount] = useState(0);

  function handleClick() {
    setCount()
    alert('You clicked me {count} times!');

  }

  return (
    <button onClick={handleClick}>Connect with your 42 account</button>
  );
}

function Login() {

  let title : string = "ft_transcendence";
  let description : string = "Connect to play Pong versus other players and show everyone how good you are !";

  return (
    <div>
      <h1>{title}</h1>
      <h2>{description}</h2>
      <MyButton/>
    </div>
  );
}

function Home() {

  return (
    <div>
      <h1>Home</h1>
    </div>
  );
}

function App() {

  let content;
  let isLogged : boolean = false;

  if (isLogged === false)
  {
    content = <Login/>
  }
  else
  {
    content = <Home/>
  }

  return (
    <div>
      {content}
    </div>
  );
}

export default App;
